import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import { Persona } from '../types';
import { getPersona, updatePersona } from '../api';
import { avatars } from '../data/avatars';

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [persona, setPersona] = useState<Persona | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [editedPersona, setEditedPersona] = useState<Persona | null>(null);

  useEffect(() => {
    const fetchPersona = async () => {
      if (id) {
        try {
          const data = await getPersona(id);
          setPersona(data);
          setEditedPersona(data);
          setIsLoading(false);
        } catch (error) {
          console.error('Error fetching persona:', error);
          setIsLoading(false);
        }
      }
    };

    fetchPersona();
  }, [id]);

  const handleSave = async () => {
    if (editedPersona) {
      try {
        await updatePersona(editedPersona.id, editedPersona);
        setPersona(editedPersona);
        setIsEditing(false);
      } catch (error) {
        console.error('Error updating persona:', error);
      }
    }
  };

  const getStyleClasses = (style: string) => {
    switch (style) {
      case 'minimal':
        return {
          card: 'bg-minimal-50 border-minimal-200 shadow-lg',
          header: 'bg-minimal-100 text-minimal-900',
          content: 'text-minimal-800',
          accent: 'text-minimal-600',
          button: 'bg-minimal-200 hover:bg-minimal-300 text-minimal-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(45deg,#f5f5f5_25%,transparent_25%)] before:opacity-10'
        };
      case 'fantasy':
        return {
          card: 'bg-fantasy-50 border-fantasy-200 shadow-lg',
          header: 'bg-fantasy-100 text-fantasy-900',
          content: 'text-fantasy-800',
          accent: 'text-fantasy-600',
          button: 'bg-fantasy-200 hover:bg-fantasy-300 text-fantasy-900',
          pattern: 'before:absolute before:inset-0 before:bg-[radial-gradient(circle_at_center,#86efac_0%,transparent_70%)] before:opacity-10'
        };
      case 'business':
        return {
          card: 'bg-business-50 border-business-200 shadow-lg',
          header: 'bg-business-100 text-business-900',
          content: 'text-business-800',
          accent: 'text-business-600',
          button: 'bg-business-200 hover:bg-business-300 text-business-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(90deg,#93c5fd_1px,transparent_1px)] before:opacity-10'
        };
      case 'scifi':
        return {
          card: 'bg-scifi-50 border-scifi-200 shadow-lg',
          header: 'bg-scifi-100 text-scifi-900',
          content: 'text-scifi-800',
          accent: 'text-scifi-600',
          button: 'bg-scifi-200 hover:bg-scifi-300 text-scifi-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(45deg,#d8b4fe_25%,transparent_25%)] before:opacity-10'
        };
      default:
        return {
          card: 'bg-white border-gray-200 shadow-lg',
          header: 'bg-gray-100 text-gray-900',
          content: 'text-gray-800',
          accent: 'text-gray-600',
          button: 'bg-gray-200 hover:bg-gray-300 text-gray-900',
          pattern: ''
        };
    }
  };

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Загрузка...</div>;
  }

  if (!persona) {
    return <div className="flex justify-center items-center h-screen">Персона не найдена</div>;
  }

  const styleClasses = getStyleClasses(persona.style);

  return (
    <div className="min-h-screen p-6 flex items-center justify-center">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className={`relative max-w-2xl w-full rounded-lg border-2 ${styleClasses.card} ${styleClasses.pattern} overflow-hidden`}
      >
        <div className={`p-6 ${styleClasses.header} relative`}>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div 
                className="w-20 h-20 rounded-full overflow-hidden bg-white p-1"
                dangerouslySetInnerHTML={{ 
                  __html: avatars.find(a => a.id === persona.avatarUrl)?.svg || '' 
                }}
              />
              <div>
                <h1 className="text-2xl font-bold">{persona.name}</h1>
                <p className={`text-sm ${styleClasses.accent}`}>{persona.role}</p>
              </div>
            </div>
            <button
              onClick={() => setIsEditing(!isEditing)}
              className={`px-4 py-2 rounded-lg ${styleClasses.button} transition-colors duration-200`}
            >
              {isEditing ? 'Сохранить' : 'Редактировать'}
            </button>
          </div>
        </div>

        <div className="p-6">
          {isEditing ? (
            <div className="space-y-4">
              <div>
                <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                  Имя
                </label>
                <input
                  type="text"
                  value={editedPersona?.name || ''}
                  onChange={(e) => setEditedPersona({ ...editedPersona!, name: e.target.value })}
                  className={`w-full p-2 rounded-lg border ${styleClasses.card} ${styleClasses.content}`}
                />
              </div>
              <div>
                <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                  Роль
                </label>
                <input
                  type="text"
                  value={editedPersona?.role || ''}
                  onChange={(e) => setEditedPersona({ ...editedPersona!, role: e.target.value })}
                  className={`w-full p-2 rounded-lg border ${styleClasses.card} ${styleClasses.content}`}
                />
              </div>
              <div>
                <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                  Описание
                </label>
                <textarea
                  value={editedPersona?.description || ''}
                  onChange={(e) => setEditedPersona({ ...editedPersona!, description: e.target.value })}
                  className={`w-full p-2 rounded-lg border ${styleClasses.card} ${styleClasses.content}`}
                  rows={4}
                />
              </div>
              <button
                onClick={handleSave}
                className={`w-full px-4 py-2 rounded-lg ${styleClasses.button} transition-colors duration-200`}
              >
                Сохранить изменения
              </button>
            </div>
          ) : (
            <div className={`space-y-4 ${styleClasses.content}`}>
              <div>
                <h2 className={`text-lg font-semibold ${styleClasses.accent} mb-2`}>
                  О персонаже
                </h2>
                <p className="text-justify">{persona.description}</p>
              </div>
              <div>
                <h2 className={`text-lg font-semibold ${styleClasses.accent} mb-2`}>
                  Характеристики
                </h2>
                <div className="grid grid-cols-2 gap-4">
                  {persona.characteristics.map((char, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <span className="font-medium">{char.name}:</span>
                      <span>{char.value}</span>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </motion.div>
    </div>
  );
};

export default Card;
