import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Persona, Characteristic } from '../types';
import { getPersona, updatePersona } from '../api';
import { Avatar, avatars } from '../data/avatars';

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [persona, setPersona] = useState<Persona | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [editedPersona, setEditedPersona] = useState<Persona | null>(null);

  useEffect(() => {
    const fetchPersona = async () => {
      if (id) {
        try {
          const data = await getPersona(id);
          setPersona(data);
          setEditedPersona(data);
          setIsLoading(false);
        } catch (error) {
          console.error('Error fetching persona:', error);
          setIsLoading(false);
        }
      }
    };

    fetchPersona();
  }, [id]);

  const handleSave = async () => {
    if (editedPersona) {
      try {
        await updatePersona(editedPersona.id, editedPersona);
        setPersona(editedPersona);
        setIsEditing(false);
      } catch (error) {
        console.error('Error updating persona:', error);
      }
    }
  };

  const getStyleClasses = (style: string) => {
    switch (style) {
      case 'minimal':
        return {
          card: 'bg-minimal-50 border-minimal-200 shadow-lg',
          header: 'bg-minimal-100 text-minimal-900',
          content: 'text-minimal-800',
          accent: 'text-minimal-600',
          button: 'bg-minimal-200 hover:bg-minimal-300 text-minimal-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(45deg,#f5f5f5_25%,transparent_25%)] before:opacity-10',
          gradient: 'from-minimal-50 to-minimal-100',
          decoration: 'after:absolute after:inset-0 after:bg-[radial-gradient(circle_at_center,#f5f5f5_0%,transparent_70%)] after:opacity-5',
          border: 'border-2 border-minimal-200',
          shadow: 'shadow-[0_4px_6px_-1px_rgba(0,0,0,0.1),0_2px_4px_-1px_rgba(0,0,0,0.06)]'
        };
      case 'fantasy':
        return {
          card: 'bg-fantasy-50 border-fantasy-200 shadow-lg',
          header: 'bg-fantasy-100 text-fantasy-900',
          content: 'text-fantasy-800',
          accent: 'text-fantasy-600',
          button: 'bg-fantasy-200 hover:bg-fantasy-300 text-fantasy-900',
          pattern: 'before:absolute before:inset-0 before:bg-[radial-gradient(circle_at_center,#86efac_0%,transparent_70%)] before:opacity-10',
          gradient: 'from-fantasy-50 to-fantasy-100',
          decoration: 'after:absolute after:inset-0 after:bg-[linear-gradient(45deg,#86efac_25%,transparent_25%)] after:opacity-5',
          border: 'border-2 border-fantasy-200',
          shadow: 'shadow-[0_4px_6px_-1px_rgba(134,239,172,0.3),0_2px_4px_-1px_rgba(134,239,172,0.2)]'
        };
      case 'business':
        return {
          card: 'bg-business-50 border-business-200 shadow-lg',
          header: 'bg-business-100 text-business-900',
          content: 'text-business-800',
          accent: 'text-business-600',
          button: 'bg-business-200 hover:bg-business-300 text-business-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(90deg,#93c5fd_1px,transparent_1px)] before:opacity-10',
          gradient: 'from-business-50 to-business-100',
          decoration: 'after:absolute after:inset-0 after:bg-[linear-gradient(45deg,#93c5fd_25%,transparent_25%)] after:opacity-5',
          border: 'border-2 border-business-200',
          shadow: 'shadow-[0_4px_6px_-1px_rgba(147,197,253,0.3),0_2px_4px_-1px_rgba(147,197,253,0.2)]'
        };
      case 'scifi':
        return {
          card: 'bg-black/40 backdrop-blur-md border-scifi-400/30 shadow-lg',
          header: 'bg-black/50 text-scifi-100 backdrop-blur-lg border-b border-scifi-400/30',
          content: 'text-scifi-200',
          accent: 'text-scifi-400',
          button: 'bg-scifi-900/80 hover:bg-scifi-800/80 text-scifi-100 backdrop-blur-sm border border-scifi-400/50',
          pattern: `
            before:absolute before:inset-0 
            before:bg-[linear-gradient(90deg,transparent_49%,rgba(216,180,254,0.1)_49%,rgba(216,180,254,0.1)_51%,transparent_51%),linear-gradient(0deg,transparent_49%,rgba(216,180,254,0.1)_49%,rgba(216,180,254,0.1)_51%,transparent_51%)]
            before:bg-[length:30px_30px] before:opacity-30
          `,
          gradient: 'from-black via-scifi-900/20 to-scifi-800/30',
          decoration: `
            after:absolute after:inset-0 
            after:bg-[radial-gradient(circle_at_30%_20%,rgba(216,180,254,0.4)_0%,transparent_50%),radial-gradient(circle_at_70%_80%,rgba(216,180,254,0.4)_0%,transparent_50%)] 
            after:opacity-20 
            after:animate-pulse
          `,
          border: 'border border-scifi-400/30 ring-1 ring-scifi-500/20',
          shadow: 'shadow-[0_0_30px_-5px_rgba(216,180,254,0.3)] hover:shadow-[0_0_40px_-5px_rgba(216,180,254,0.4)]',
          input: 'bg-black/30 border-scifi-400/30 focus:border-scifi-400/50 focus:ring-scifi-400/30 text-scifi-100 placeholder-scifi-600/50',
          characteristic: 'bg-black/40 backdrop-blur-sm border border-scifi-400/30 rounded-lg',
          glow: 'after:absolute after:inset-0 after:bg-scifi-400/20 after:blur-xl after:opacity-0 hover:after:opacity-100 after:transition-opacity',
          scanline: `
            before:absolute before:inset-0 
            before:bg-[linear-gradient(to_bottom,transparent_50%,rgba(216,180,254,0.05)_50%)] 
            before:bg-[length:100%_4px] 
            before:animate-scanline 
            before:pointer-events-none
          `,
          neonText: 'text-transparent bg-clip-text bg-gradient-to-r from-scifi-300 to-scifi-500 animate-pulse',
          hologram: `
            after:absolute after:inset-0 
            after:bg-[linear-gradient(45deg,transparent_45%,rgba(216,180,254,0.1)_45%,rgba(216,180,254,0.1)_55%,transparent_55%)] 
            after:bg-[length:10px_10px] 
            after:animate-hologram
          `
        };
      default:
        return {
          card: 'bg-white border-gray-200 shadow-lg',
          header: 'bg-gray-100 text-gray-900',
          content: 'text-gray-800',
          accent: 'text-gray-600',
          button: 'bg-gray-200 hover:bg-gray-300 text-gray-900',
          pattern: '',
          gradient: 'from-gray-50 to-gray-100',
          decoration: '',
          border: 'border-2 border-gray-200',
          shadow: 'shadow-[0_4px_6px_-1px_rgba(0,0,0,0.1),0_2px_4px_-1px_rgba(0,0,0,0.06)]'
        };
    }
  };

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Загрузка...</div>;
  }

  if (!persona) {
    return <div className="flex justify-center items-center h-screen">Персона не найдена</div>;
  }

  const styleClasses = getStyleClasses(persona.style);

  const selectedAvatar = avatars.find((a: Avatar) => a.id === persona.avatarUrl);

  return (
    <div className="min-h-screen p-6 flex items-center justify-center bg-gradient-to-b from-black to-scifi-900">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        whileHover={{ scale: 1.01 }}
        transition={{ duration: 0.3 }}
        className={`relative max-w-2xl w-full rounded-lg ${styleClasses.border} ${styleClasses.card} ${styleClasses.pattern} ${styleClasses.decoration} ${styleClasses.shadow} ${persona.style === 'scifi' ? `${styleClasses.scanline} ${styleClasses.hologram}` : ''} overflow-hidden backdrop-blur-lg`}
      >
        <motion.div 
          className={`absolute inset-0 ${styleClasses.pattern} opacity-10`}
          animate={{ 
            backgroundPosition: ['0% 0%', '100% 100%'],
          }}
          transition={{ 
            duration: persona.style === 'scifi' ? 10 : 20,
            repeat: Infinity,
            repeatType: "reverse"
          }}
        />
        <motion.div 
          className={`absolute inset-0 bg-gradient-to-br ${styleClasses.gradient} opacity-20`}
          animate={{ 
            opacity: [0.2, 0.3, 0.2],
          }}
          transition={{ 
            duration: 5,
            repeat: Infinity,
            repeatType: "reverse"
          }}
        />
        <motion.div
          className="absolute inset-0 bg-white opacity-0"
          whileHover={{ opacity: 0.05 }}
          transition={{ duration: 0.3 }}
        />
        <div className={`p-6 ${styleClasses.header} relative ${persona.style === 'scifi' ? 'border-b border-scifi-400/30' : ''}`}>
          <div className="flex items-center justify-between">
            <motion.div 
              className="flex items-center space-x-4"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.2 }}
            >
              <motion.div 
                className={`w-20 h-20 rounded-full overflow-hidden bg-black/50 p-1 relative group ${styleClasses.border} ${persona.style === 'scifi' ? styleClasses.glow : ''}`}
                whileHover={{ scale: 1.1 }}
              >
                {selectedAvatar && (
                  <div 
                    className="w-full h-full"
                    dangerouslySetInnerHTML={{ __html: selectedAvatar.svg }}
                  />
                )}
              </motion.div>
              <div>
                <motion.h1 
                  className={`text-2xl font-bold ${persona.style === 'scifi' ? styleClasses.neonText : styleClasses.accent}`}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  {persona.name}
                </motion.h1>
                <motion.p 
                  className={`text-sm ${styleClasses.accent}`}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.4 }}
                >
                  {persona.role}
                </motion.p>
              </div>
            </motion.div>
            <motion.button
              onClick={() => setIsEditing(!isEditing)}
              className={`px-4 py-2 rounded-lg ${styleClasses.button} transition-colors duration-200 relative overflow-hidden group ${styleClasses.border} ${persona.style === 'scifi' ? styleClasses.glow : ''}`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <motion.span
                className="absolute inset-0 bg-white opacity-0 group-hover:opacity-10 transition-opacity duration-300"
              />
              <motion.span
                className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-0 group-hover:opacity-20"
                animate={{
                  x: ['-100%', '100%']
                }}
                transition={{
                  duration: 1.5,
                  repeat: Infinity,
                  ease: "linear"
                }}
              />
              <span className="relative z-10">
                {isEditing ? 'Сохранить' : 'Редактировать'}
              </span>
            </motion.button>
          </div>
        </div>

        <div className="p-6">
          <AnimatePresence mode="wait">
            {isEditing ? (
              <motion.div
                key="edit"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-4"
              >
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                    Имя
                  </label>
                  <motion.input
                    type="text"
                    value={editedPersona?.name || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, name: e.target.value })}
                    className={`w-full p-2 rounded-lg ${styleClasses.input} ${styleClasses.content} focus:outline-none focus:ring-2 focus:ring-opacity-50`}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                    Роль
                  </label>
                  <motion.input
                    type="text"
                    value={editedPersona?.role || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, role: e.target.value })}
                    className={`w-full p-2 rounded-lg ${styleClasses.input} ${styleClasses.content} focus:outline-none focus:ring-2 focus:ring-opacity-50`}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                    Описание
                  </label>
                  <motion.textarea
                    value={editedPersona?.description || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, description: e.target.value })}
                    className={`w-full p-2 rounded-lg ${styleClasses.input} ${styleClasses.content} focus:outline-none focus:ring-2 focus:ring-opacity-50`}
                    rows={4}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.button
                  onClick={handleSave}
                  className={`w-full px-4 py-2 rounded-lg ${styleClasses.button} transition-colors duration-200 relative overflow-hidden group ${styleClasses.border} ${persona.style === 'scifi' ? styleClasses.glow : ''}`}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <motion.span
                    className="absolute inset-0 bg-white opacity-0 group-hover:opacity-10 transition-opacity duration-300"
                  />
                  <span className="relative z-10">Сохранить изменения</span>
                </motion.button>
              </motion.div>
            ) : (
              <motion.div
                key="view"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className={`space-y-4 ${styleClasses.content}`}
              >
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <h2 className={`text-lg font-semibold ${persona.style === 'scifi' ? styleClasses.neonText : styleClasses.accent} mb-2`}>
                    О персонаже
                  </h2>
                  <motion.p 
                    className="text-justify"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                  >
                    {persona.description}
                  </motion.p>
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <h2 className={`text-lg font-semibold ${persona.style === 'scifi' ? styleClasses.neonText : styleClasses.accent} mb-2`}>
                    Характеристики
                  </h2>
                  <div className="grid grid-cols-2 gap-4">
                    {persona.characteristics.map((char: Characteristic, index: number) => (
                      <motion.div
                        key={index}
                        className={`flex items-center space-x-2 p-2 relative overflow-hidden ${styleClasses.characteristic} ${persona.style === 'scifi' ? styleClasses.glow : ''}`}
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.1 * index }}
                        whileHover={{ scale: 1.02 }}
                      >
                        <motion.span
                          className="absolute inset-0 bg-white opacity-0 group-hover:opacity-10"
                          whileHover={{ opacity: 0.1 }}
                        />
                        <span className="font-medium relative z-10">{char.name}:</span>
                        <span className="relative z-10">{char.value}</span>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.div>
    </div>
  );
};

export default Card;
