import React, { useState } from 'react';
import { motion } from 'framer-motion';

interface AvatarSelectorProps {
  onSelect: (avatarUrl: string) => void;
  style: 'minimal' | 'fantasy' | 'business' | 'scifi';
}

const AvatarSelector: React.FC<AvatarSelectorProps> = ({ onSelect, style }) => {
  const [selectedAvatar, setSelectedAvatar] = useState<string | null>(null);

  // Временные данные для демонстрации
  const avatars = [
    { id: '1', url: 'https://via.placeholder.com/150?text=Avatar+1' },
    { id: '2', url: 'https://via.placeholder.com/150?text=Avatar+2' },
    { id: '3', url: 'https://via.placeholder.com/150?text=Avatar+3' },
    { id: '4', url: 'https://via.placeholder.com/150?text=Avatar+4' }
  ];

  const handleSelect = (avatarUrl: string) => {
    setSelectedAvatar(avatarUrl);
    onSelect(avatarUrl);
  };

  return (
    <div className="space-y-6">
      <h3 className="text-xl font-semibold text-purple-900">
        Выберите аватар
      </h3>

      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        {avatars.map((avatar) => (
          <motion.button
            key={avatar.id}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => handleSelect(avatar.url)}
            className={`relative rounded-lg overflow-hidden ${
              selectedAvatar === avatar.url ? 'ring-2 ring-purple-600' : ''
            }`}
          >
            <img
              src={avatar.url}
              alt={`Аватар ${avatar.id}`}
              className="w-full h-full object-cover"
            />
            {selectedAvatar === avatar.url && (
              <div className="absolute inset-0 bg-purple-600 bg-opacity-20 flex items-center justify-center">
                <span className="text-white text-2xl">✓</span>
              </div>
            )}
          </motion.button>
        ))}
      </div>

      <div className="text-center">
        <button
          onClick={() => {
            // Здесь будет логика генерации аватара через API
            const generatedAvatar = 'https://via.placeholder.com/150?text=Generated';
            handleSelect(generatedAvatar);
          }}
          className="text-purple-600 hover:text-purple-700 font-medium"
        >
          Сгенерировать новый аватар
        </button>
      </div>
    </div>
  );
};

export default AvatarSelector;
