import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import SettingSelector from '../components/SettingSelector';
import ProgressBar from '../components/ProgressBar';
import AvatarSelector from '../components/AvatarSelector';
import { Question, PersonaCard } from '../types';
import { createPersona } from '../services/personaService';
import { auth } from '../firebase/config';

const questions: Question[] = [
  {
    id: 1,
    text: "Какая основная цель вашего клиента?",
    options: ["Продвижение по карьере", "Финансовая стабильность", "Личностный рост", "Семейное благополучие"],
    category: "goals"
  },
  {
    id: 2,
    text: "Что больше всего беспокоит вашего клиента?",
    options: ["Нехватка времени", "Финансовые проблемы", "Неопределенность будущего", "Отсутствие баланса"],
    category: "pains"
  }
];

const Play: React.FC = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [selectedSetting, setSelectedSetting] = useState<'minimal' | 'fantasy' | 'business' | 'scifi' | null>(null);
  const [selectedAvatar, setSelectedAvatar] = useState<string | null>(null);
  const [answers, setAnswers] = useState<Record<string, string>>({});
  const navigate = useNavigate();

  const handleAnswer = (answer: string) => {
    const currentQuestion = questions[currentStep - 1];
    setAnswers(prev => ({
      ...prev,
      [currentQuestion.id.toString()]: answer
    }));

    if (currentStep < questions.length) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleSettingSelect = (setting: 'minimal' | 'fantasy' | 'business' | 'scifi') => {
    setSelectedSetting(setting);
    setCurrentStep(1);
  };

  const handleAvatarSelect = (avatarUrl: string) => {
    setSelectedAvatar(avatarUrl);
  };

  const handleComplete = async () => {
    if (!auth.currentUser) {
      navigate('/login');
      return;
    }

    const persona: Omit<PersonaCard, 'id'> = {
      uid: auth.currentUser.uid,
      name: 'Новый персонаж',
      age: 30,
      job: 'Профессия',
      goals: [answers['1']],
      pains: [answers['2']],
      quote: 'Цитата персонажа',
      avatarUrl: selectedAvatar || '',
      style: selectedSetting || 'minimal',
      createdAt: new Date().toISOString(),
      questionsMeta: answers
    };

    const personaId = await createPersona(persona);
    navigate(`/card/${personaId}`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-100 to-white py-16">
      <div className="container mx-auto px-4">
        <div className="max-w-2xl mx-auto">
          <ProgressBar currentStep={currentStep} totalSteps={questions.length + 1} />

          <AnimatePresence mode="wait">
            {currentStep === 0 && (
              <motion.div
                key="setting"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
              >
                <h2 className="text-2xl font-bold text-purple-900 mb-6">
                  Выберите стиль персонажа
                </h2>
                <SettingSelector onSelect={handleSettingSelect} />
              </motion.div>
            )}

            {currentStep > 0 && currentStep <= questions.length && (
              <motion.div
                key="question"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="bg-white rounded-lg shadow-lg p-8"
              >
                <h2 className="text-2xl font-bold text-purple-900 mb-6">
                  {questions[currentStep - 1].text}
                </h2>
                <div className="space-y-4">
                  {questions[currentStep - 1].options.map((option, index) => (
                    <button
                      key={index}
                      onClick={() => handleAnswer(option)}
                      className="w-full text-left p-4 border border-purple-200 rounded-lg hover:bg-purple-50 transition-colors"
                    >
                      {option}
                    </button>
                  ))}
                </div>
              </motion.div>
            )}

            {currentStep > questions.length && (
              <motion.div
                key="avatar"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="bg-white rounded-lg shadow-lg p-8"
              >
                <AvatarSelector
                  onSelect={handleAvatarSelect}
                  style={selectedSetting || 'minimal'}
                />
                <div className="mt-8 text-center">
                  <button
                    onClick={handleComplete}
                    disabled={!selectedAvatar}
                    className="bg-purple-600 text-white px-8 py-3 rounded-full text-lg font-semibold hover:bg-purple-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Создать персонажа
                  </button>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

export default Play;
