import React, { useState } from 'react';
import { avatars } from '../data/avatars';

interface AvatarSelectorProps {
  onSelect: (avatarUrl: string) => void;
}

const AvatarSelector: React.FC<AvatarSelectorProps> = ({ onSelect }) => {
  const [selectedAvatar, setSelectedAvatar] = useState<string | null>(null);

  const handleSelect = (avatarId: string) => {
    setSelectedAvatar(avatarId);
    onSelect(avatarId);
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-8">
      <h2 className="text-2xl font-bold text-purple-900 mb-6">
        Выберите аватар персонажа
      </h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {avatars.map((avatar) => (
          <button
            key={avatar.id}
            onClick={() => handleSelect(avatar.id)}
            className={`p-2 border-2 rounded-lg transition-all ${
              selectedAvatar === avatar.id
                ? 'border-purple-600 scale-105'
                : 'border-transparent hover:border-purple-200'
            }`}
          >
            <div className="w-full h-32 flex flex-col items-center justify-center">
              <div className="w-24 h-24 rounded-full overflow-hidden bg-gradient-to-br from-purple-50 to-purple-100">
                <img 
                  src={avatar.url} 
                  alt={avatar.description || 'Аватар'} 
                  className="w-full h-full object-cover"
                />
              </div>
              <span className="mt-2 text-sm text-gray-600">{avatar.description}</span>
            </div>
          </button>
        ))}
      </div>
    </div>
  );
};

export default AvatarSelector;
