import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { PersonaCard } from '../types';
import { getPersonasByUser, updatePersona, deletePersona } from '../services/personaService';

const avatars = [
  {
    id: '1',
    url: '/avatars/avatar1.png',
    description: 'Молодой мужчина'
  },
  {
    id: '2',
    url: '/avatars/avatar2.png',
    description: 'Молодая женщина'
  },
  {
    id: '3',
    url: '/avatars/avatar3.png',
    description: 'Мужчина средних лет'
  },
  {
    id: '4',
    url: '/avatars/avatar4.png',
    description: 'Женщина средних лет'
  },
  {
    id: '5',
    url: '/avatars/avatar5.png',
    description: 'Пожилой мужчина (70+)'
  },
  {
    id: '6',
    url: '/avatars/avatar6.png',
    description: 'Пожилая женщина (70+)'
  }
];

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [persona, setPersona] = useState<PersonaCard | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedPersona, setEditedPersona] = useState<Partial<PersonaCard>>({});

  useEffect(() => {
    const loadPersona = async () => {
      const personas = await getPersonasByUser();
      const foundPersona = personas.find(p => p.id === id);
      if (foundPersona) {
        setPersona(foundPersona);
        setEditedPersona(foundPersona);
      }
    };
    loadPersona();
  }, [id]);

  const handleSave = async () => {
    if (persona) {
      await updatePersona(persona.id, editedPersona);
      setPersona({ ...persona, ...editedPersona });
      setIsEditing(false);
    }
  };

  const handleDelete = async () => {
    if (persona) {
      await deletePersona(persona.id);
      navigate('/profile');
    }
  };

  if (!persona) {
    return <div>Загрузка...</div>;
  }

  const selectedAvatar = avatars.find(a => a.id === persona.avatarUrl);

  return (
    <div className={`min-h-screen bg-gradient-to-b from-${persona.style}-100 to-white py-16`}>
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className={`bg-white rounded-lg shadow-lg overflow-hidden border-2 border-${persona.style}-200`}>
            <div className={`relative bg-${persona.style}-50 p-8`}>
              <div className="flex justify-center">
                <img
                  src={selectedAvatar?.url}
                  alt={selectedAvatar?.description}
                  className={`w-32 h-32 object-cover rounded-full border-4 border-${persona.style}-300`}
                />
              </div>
              <div className="absolute top-4 right-4 space-x-2">
                <button
                  onClick={() => setIsEditing(!isEditing)}
                  className={`bg-${persona.style}-600 text-white px-4 py-2 rounded-lg hover:bg-${persona.style}-700 transition-colors`}
                >
                  {isEditing ? 'Отменить' : 'Редактировать'}
                </button>
                <button
                  onClick={handleDelete}
                  className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
                >
                  Удалить
                </button>
              </div>
            </div>

            <div className="p-8">
              {isEditing ? (
                <div className="space-y-4">
                  <div>
                    <label className={`block text-${persona.style}-900 text-sm font-bold mb-2`}>
                      Имя
                    </label>
                    <input
                      type="text"
                      value={editedPersona.name || ''}
                      onChange={(e) =>
                        setEditedPersona({ ...editedPersona, name: e.target.value })
                      }
                      className={`w-full p-2 border border-${persona.style}-200 rounded-lg`}
                    />
                  </div>
                  <div>
                    <label className={`block text-${persona.style}-900 text-sm font-bold mb-2`}>
                      Возраст
                    </label>
                    <input
                      type="number"
                      value={editedPersona.age || ''}
                      onChange={(e) =>
                        setEditedPersona({
                          ...editedPersona,
                          age: parseInt(e.target.value),
                        })
                      }
                      className={`w-full p-2 border border-${persona.style}-200 rounded-lg`}
                    />
                  </div>
                  <div>
                    <label className={`block text-${persona.style}-900 text-sm font-bold mb-2`}>
                      Профессия
                    </label>
                    <input
                      type="text"
                      value={editedPersona.job || ''}
                      onChange={(e) =>
                        setEditedPersona({ ...editedPersona, job: e.target.value })
                      }
                      className={`w-full p-2 border border-${persona.style}-200 rounded-lg`}
                    />
                  </div>
                  <button
                    onClick={handleSave}
                    className={`bg-${persona.style}-600 text-white px-6 py-2 rounded-lg hover:bg-${persona.style}-700 transition-colors`}
                  >
                    Сохранить
                  </button>
                </div>
              ) : (
                <div className="space-y-6">
                  <h1 className={`text-3xl font-bold text-${persona.style}-900`}>
                    {persona.name}
                  </h1>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <h3 className={`text-lg font-semibold text-${persona.style}-700`}>Возраст</h3>
                      <p className={`text-${persona.style}-600`}>{persona.age}</p>
                    </div>
                    <div>
                      <h3 className={`text-lg font-semibold text-${persona.style}-700`}>Профессия</h3>
                      <p className={`text-${persona.style}-600`}>{persona.job}</p>
                    </div>
                  </div>
                  <div>
                    <h3 className={`text-lg font-semibold text-${persona.style}-700`}>Цели</h3>
                    <ul className={`list-disc list-inside text-${persona.style}-600`}>
                      {persona.goals.map((goal, index) => (
                        <li key={index}>{goal}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h3 className={`text-lg font-semibold text-${persona.style}-700`}>Проблемы</h3>
                    <ul className={`list-disc list-inside text-${persona.style}-600`}>
                      {persona.pains.map((pain, index) => (
                        <li key={index}>{pain}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h3 className={`text-lg font-semibold text-${persona.style}-700`}>Цитата</h3>
                    <p className={`text-${persona.style}-600 italic`}>"{persona.quote}"</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
