import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { PersonaCard } from '../types/index';
import { getPersonasByUser } from '../services/personaService';
import { avatars } from '../data/avatars';

const Profile: React.FC = () => {
  const [personas, setPersonas] = useState<PersonaCard[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    const loadPersonas = async () => {
      const loadedPersonas = await getPersonasByUser();
      setPersonas(loadedPersonas);
    };
    loadPersonas();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-100 to-white py-16">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className="flex justify-between items-center mb-8">
            <h1 className="text-3xl font-bold text-purple-900">Мои персонажи</h1>
            <button
              onClick={() => navigate('/play')}
              className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700 transition-colors"
            >
              Создать нового
            </button>
          </div>

          {personas.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-600 text-lg mb-4">
                У вас пока нет созданных персонажей
              </p>
              <button
                onClick={() => navigate('/play')}
                className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700 transition-colors"
              >
                Создать первого персонажа
              </button>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {personas.map((persona) => {
                const selectedAvatar = avatars.find(a => a.id === persona.avatarUrl);
                return (
                  <div
                    key={persona.id}
                    className={`bg-white rounded-lg shadow-lg overflow-hidden hover:shadow-xl transition-shadow cursor-pointer border-2 border-${persona.style}-200`}
                    onClick={() => navigate(`/card/${persona.id}`)}
                  >
                    <div className={`bg-${persona.style}-50 p-6`}>
                      <div className="flex justify-center">
                        <img
                          src={selectedAvatar?.url}
                          alt={selectedAvatar?.description}
                          className={`w-24 h-24 object-cover rounded-full border-4 border-${persona.style}-300`}
                        />
                      </div>
                    </div>
                    <div className="p-6">
                      <h2 className={`text-xl font-bold text-${persona.style}-900 mb-2`}>
                        {persona.name}
                      </h2>
                      <p className={`text-${persona.style}-600`}>{persona.job}</p>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
