import React from 'react';
import { motion } from 'framer-motion';
import { avatars } from '../data/avatars';

interface AvatarSelectorProps {
  onSelect: (avatarUrl: string) => void;
}

const AvatarSelector: React.FC<AvatarSelectorProps> = ({ onSelect }) => {
  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-purple-900 text-center">
        Выберите аватар для вашего клиента
      </h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-6">
        {avatars.map((avatar) => (
          <motion.button
            key={avatar.id}
            onClick={() => onSelect(avatar.id)}
            className="relative group"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            <div className="aspect-square rounded-xl overflow-hidden bg-gradient-to-br from-purple-100 to-white p-4 shadow-lg group-hover:shadow-xl transition-shadow">
              <div 
                className="w-full h-full"
                dangerouslySetInnerHTML={{ __html: avatar.svg }}
              />
            </div>
            <p className="mt-2 text-sm text-purple-700 text-center">
              {avatar.description}
            </p>
          </motion.button>
        ))}
      </div>
    </div>
  );
};

export default AvatarSelector;
