import React, { useState } from 'react';

interface AvatarSelectorProps {
  onSelect: (avatarUrl: string) => void;
}

const avatars = [
  {
    id: '1',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <circle cx="50" cy="35" r="20" fill="#8B5CF6"/>
      <circle cx="50" cy="100" r="40" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '2',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <rect x="25" y="25" width="50" height="50" rx="10" fill="#8B5CF6"/>
      <rect x="20" y="85" width="60" height="20" rx="10" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '3',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <path d="M50 20 L20 80 L80 80 Z" fill="#8B5CF6"/>
      <circle cx="50" cy="90" r="10" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '4',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <circle cx="50" cy="50" r="40" fill="#8B5CF6"/>
      <circle cx="50" cy="50" r="20" fill="#fff"/>
    </svg>`
  },
  {
    id: '5',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <rect x="20" y="20" width="60" height="60" rx="30" fill="#8B5CF6"/>
      <rect x="30" y="80" width="40" height="20" rx="10" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '6',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <path d="M50 20 Q70 50 50 80 Q30 50 50 20" fill="#8B5CF6"/>
      <circle cx="50" cy="90" r="10" fill="#8B5CF6"/>
    </svg>`
  }
];

const AvatarSelector: React.FC<AvatarSelectorProps> = ({ onSelect }) => {
  const [selectedAvatar, setSelectedAvatar] = useState<string | null>(null);

  const handleSelect = (avatarId: string) => {
    setSelectedAvatar(avatarId);
    onSelect(avatarId);
  };

  return (
    <div className="bg-white rounded-lg shadow-lg p-8">
      <h2 className="text-2xl font-bold text-purple-900 mb-6">
        Выберите аватар персонажа
      </h2>
      <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
        {avatars.map((avatar) => (
          <button
            key={avatar.id}
            onClick={() => handleSelect(avatar.id)}
            className={`p-2 border-2 rounded-lg transition-all ${
              selectedAvatar === avatar.id
                ? 'border-purple-600 scale-105'
                : 'border-transparent hover:border-purple-200'
            }`}
          >
            <div 
              className="w-full h-32 flex items-center justify-center"
              dangerouslySetInnerHTML={{ __html: avatar.svg }}
            />
          </button>
        ))}
      </div>
    </div>
  );
};

export default AvatarSelector;
