import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Persona, Characteristic } from '../types/index';
import { getPersona, updatePersona } from '../api';
import { avatars, Avatar } from '../data/avatars';

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [persona, setPersona] = useState<Persona | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [editedPersona, setEditedPersona] = useState<Persona | null>(null);

  useEffect(() => {
    const fetchPersona = async () => {
      if (id) {
        try {
          const data = await getPersona(id);
          console.log('Fetched persona:', data);
          setPersona(data);
          setEditedPersona(data);
          setIsLoading(false);
        } catch (error) {
          console.error('Error fetching persona:', error);
          setIsLoading(false);
        }
      }
    };

    fetchPersona();
  }, [id]);

  const handleSave = async () => {
    if (editedPersona) {
      try {
        await updatePersona(editedPersona.id, editedPersona);
        setPersona(editedPersona);
        setIsEditing(false);
      } catch (error) {
        console.error('Error updating persona:', error);
      }
    }
  };

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Загрузка...</div>;
  }

  if (!persona) {
    return <div className="flex justify-center items-center h-screen">Персона не найдена</div>;
  }

  const selectedAvatar = avatars.find((a: Avatar) => a.id === persona.avatarUrl);
  console.log('Selected avatar in Card:', selectedAvatar, 'avatarUrl:', persona.avatarUrl);

  return (
    <div className="min-h-screen p-6 flex items-center justify-center bg-gradient-to-b from-purple-100 to-white">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        whileHover={{ scale: 1.01 }}
        transition={{ duration: 0.3 }}
        className="relative max-w-2xl w-full rounded-lg bg-white shadow-lg border-2 border-purple-200 overflow-hidden"
      >
        <div className="p-6 bg-purple-50 border-b border-purple-200">
          <div className="flex items-center justify-between">
            <motion.div 
              className="flex items-center space-x-4"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.2 }}
            >
              <motion.div 
                className="w-20 h-20 rounded-full overflow-hidden bg-white p-1 relative group border-2 border-purple-200"
                whileHover={{ scale: 1.1 }}
                transition={{ type: "spring", stiffness: 400, damping: 10 }}
              >
                <motion.div
                  className="absolute inset-0 bg-gradient-to-br from-transparent to-purple-100 opacity-20"
                  animate={{ rotate: [0, 360] }}
                  transition={{ duration: 10, repeat: Infinity, ease: "linear" }}
                />
                {selectedAvatar ? (
                  <motion.img
                    src={selectedAvatar.url}
                    alt={selectedAvatar.description || `Аватар ${persona.name}`}
                    className="w-full h-full object-cover relative z-10 rounded-full"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ duration: 0.3 }}
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center text-purple-300">
                    <span className="text-3xl">?</span>
                  </div>
                )}
              </motion.div>
              <div>
                <motion.h1 
                  className="text-2xl font-bold text-purple-900"
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  {persona.name}
                </motion.h1>
                <motion.p 
                  className="text-sm text-purple-600"
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.4 }}
                >
                  {persona.role}
                </motion.p>
              </div>
            </motion.div>
            <motion.button
              onClick={() => setIsEditing(!isEditing)}
              className="px-4 py-2 rounded-lg bg-purple-600 text-white hover:bg-purple-700 transition-colors"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              {isEditing ? 'Сохранить' : 'Редактировать'}
            </motion.button>
          </div>
        </div>

        <div className="p-6">
          <AnimatePresence mode="wait">
            {isEditing ? (
              <motion.div
                key="edit"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-4"
              >
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <label className="block text-sm font-medium text-purple-600 mb-1">
                    Имя
                  </label>
                  <motion.input
                    type="text"
                    value={editedPersona?.name || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, name: e.target.value })}
                    className="w-full p-2 rounded-lg border border-purple-200 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <label className="block text-sm font-medium text-purple-600 mb-1">
                    Роль
                  </label>
                  <motion.input
                    type="text"
                    value={editedPersona?.role || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, role: e.target.value })}
                    className="w-full p-2 rounded-lg border border-purple-200 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  <label className="block text-sm font-medium text-purple-600 mb-1">
                    Описание
                  </label>
                  <motion.textarea
                    value={editedPersona?.description || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, description: e.target.value })}
                    className="w-full p-2 rounded-lg border border-purple-200 focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                    rows={4}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.button
                  onClick={handleSave}
                  className="w-full px-4 py-2 rounded-lg bg-purple-600 text-white hover:bg-purple-700 transition-colors"
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  Сохранить изменения
                </motion.button>
              </motion.div>
            ) : (
              <motion.div
                key="view"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-4"
              >
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <h2 className="text-lg font-semibold text-purple-900 mb-2">
                    О персонаже
                  </h2>
                  <motion.p 
                    className="text-justify text-gray-700"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                  >
                    {persona.description}
                  </motion.p>
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <h2 className="text-lg font-semibold text-purple-900 mb-2">
                    Характеристики
                  </h2>
                  <div className="grid grid-cols-2 gap-4">
                    {persona.characteristics?.map((char: Characteristic, index: number) => (
                      <motion.div
                        key={index}
                        className="flex items-center space-x-2 p-2 bg-purple-50 rounded-lg border border-purple-200"
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.1 * index }}
                        whileHover={{ scale: 1.02 }}
                      >
                        <span className="font-medium text-purple-900">{char.name}:</span>
                        <span className="text-purple-700">{char.value}</span>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.div>
    </div>
  );
};

export default Card;
