import React from 'react';
import { useParams } from 'react-router-dom';

interface PersonaCard {
  id: string;
  name: string;
  age: number;
  job: string;
  goals: string[];
  pains: string[];
  quote: string;
  avatar: string;
  style: string;
}

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();

  // Временные данные для демонстрации
  const card: PersonaCard = {
    id: id || '1',
    name: 'Эмма',
    age: 32,
    job: 'Архитектор',
    goals: ['Продвижение', 'Свобода'],
    pains: ['Перегрузка', 'Неуверенность'],
    quote: 'Я просто хочу делать красиво!',
    avatar: 'https://via.placeholder.com/150',
    style: 'minimal'
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-100 to-white py-16">
      <div className="container mx-auto px-4">
        <div className="max-w-2xl mx-auto">
          <div className="bg-white rounded-lg shadow-xl overflow-hidden">
            <div className="p-8">
              <div className="flex items-center mb-6">
                <img
                  src={card.avatar}
                  alt={card.name}
                  className="w-24 h-24 rounded-full object-cover mr-6"
                />
                <div>
                  <h2 className="text-3xl font-bold text-purple-900">{card.name}</h2>
                  <p className="text-gray-600">{card.job}, {card.age} лет</p>
                </div>
              </div>

              <div className="mb-6">
                <h3 className="text-xl font-semibold text-purple-800 mb-2">Цели</h3>
                <div className="flex flex-wrap gap-2">
                  {card.goals.map((goal, index) => (
                    <span
                      key={index}
                      className="bg-purple-100 text-purple-800 px-3 py-1 rounded-full text-sm"
                    >
                      {goal}
                    </span>
                  ))}
                </div>
              </div>

              <div className="mb-6">
                <h3 className="text-xl font-semibold text-purple-800 mb-2">Боли</h3>
                <div className="flex flex-wrap gap-2">
                  {card.pains.map((pain, index) => (
                    <span
                      key={index}
                      className="bg-red-100 text-red-800 px-3 py-1 rounded-full text-sm"
                    >
                      {pain}
                    </span>
                  ))}
                </div>
              </div>

              <div className="bg-purple-50 p-4 rounded-lg">
                <p className="text-purple-900 italic">"{card.quote}"</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
