import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { Persona, Characteristic } from '../types';
import { getPersona, updatePersona } from '../api';
import { Avatar, avatars } from '../data/avatars';

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [persona, setPersona] = useState<Persona | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [editedPersona, setEditedPersona] = useState<Persona | null>(null);

  useEffect(() => {
    const fetchPersona = async () => {
      if (id) {
        try {
          const data = await getPersona(id);
          setPersona(data);
          setEditedPersona(data);
          setIsLoading(false);
        } catch (error) {
          console.error('Error fetching persona:', error);
          setIsLoading(false);
        }
      }
    };

    fetchPersona();
  }, [id]);

  const handleSave = async () => {
    if (editedPersona) {
      try {
        await updatePersona(editedPersona.id, editedPersona);
        setPersona(editedPersona);
        setIsEditing(false);
      } catch (error) {
        console.error('Error updating persona:', error);
      }
    }
  };

  const getStyleClasses = (style: string) => {
    switch (style) {
      case 'minimal':
        return {
          card: 'bg-minimal-50 border-minimal-200 shadow-lg',
          header: 'bg-minimal-100 text-minimal-900',
          content: 'text-minimal-800',
          accent: 'text-minimal-600',
          button: 'bg-minimal-200 hover:bg-minimal-300 text-minimal-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(45deg,#f5f5f5_25%,transparent_25%)] before:opacity-10'
        };
      case 'fantasy':
        return {
          card: 'bg-fantasy-50 border-fantasy-200 shadow-lg',
          header: 'bg-fantasy-100 text-fantasy-900',
          content: 'text-fantasy-800',
          accent: 'text-fantasy-600',
          button: 'bg-fantasy-200 hover:bg-fantasy-300 text-fantasy-900',
          pattern: 'before:absolute before:inset-0 before:bg-[radial-gradient(circle_at_center,#86efac_0%,transparent_70%)] before:opacity-10'
        };
      case 'business':
        return {
          card: 'bg-business-50 border-business-200 shadow-lg',
          header: 'bg-business-100 text-business-900',
          content: 'text-business-800',
          accent: 'text-business-600',
          button: 'bg-business-200 hover:bg-business-300 text-business-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(90deg,#93c5fd_1px,transparent_1px)] before:opacity-10'
        };
      case 'scifi':
        return {
          card: 'bg-scifi-50 border-scifi-200 shadow-lg',
          header: 'bg-scifi-100 text-scifi-900',
          content: 'text-scifi-800',
          accent: 'text-scifi-600',
          button: 'bg-scifi-200 hover:bg-scifi-300 text-scifi-900',
          pattern: 'before:absolute before:inset-0 before:bg-[linear-gradient(45deg,#d8b4fe_25%,transparent_25%)] before:opacity-10'
        };
      default:
        return {
          card: 'bg-white border-gray-200 shadow-lg',
          header: 'bg-gray-100 text-gray-900',
          content: 'text-gray-800',
          accent: 'text-gray-600',
          button: 'bg-gray-200 hover:bg-gray-300 text-gray-900',
          pattern: ''
        };
    }
  };

  if (isLoading) {
    return <div className="flex justify-center items-center h-screen">Загрузка...</div>;
  }

  if (!persona) {
    return <div className="flex justify-center items-center h-screen">Персона не найдена</div>;
  }

  const styleClasses = getStyleClasses(persona.style);

  const selectedAvatar = avatars.find((a: Avatar) => a.id === persona.avatarUrl);

  return (
    <div className="min-h-screen p-6 flex items-center justify-center bg-gradient-to-b from-gray-50 to-white">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        whileHover={{ scale: 1.01 }}
        transition={{ duration: 0.3 }}
        className={`relative max-w-2xl w-full rounded-lg border-2 ${styleClasses.card} ${styleClasses.pattern} overflow-hidden`}
      >
        <motion.div 
          className={`absolute inset-0 ${styleClasses.pattern} opacity-10`}
          animate={{ 
            backgroundPosition: ['0% 0%', '100% 100%'],
          }}
          transition={{ 
            duration: 20,
            repeat: Infinity,
            repeatType: "reverse"
          }}
        />
        <motion.div 
          className={`absolute inset-0 bg-gradient-to-br ${styleClasses.gradient} opacity-20`}
          animate={{ 
            opacity: [0.2, 0.3, 0.2],
          }}
          transition={{ 
            duration: 5,
            repeat: Infinity,
            repeatType: "reverse"
          }}
        />
        <div className={`p-6 ${styleClasses.header} relative`}>
          <div className="flex items-center justify-between">
            <motion.div 
              className="flex items-center space-x-4"
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: 0.2 }}
            >
              <motion.div 
                className="w-20 h-20 rounded-full overflow-hidden bg-white p-1 relative"
                whileHover={{ scale: 1.1 }}
                transition={{ type: "spring", stiffness: 400, damping: 10 }}
              >
                <motion.div
                  className="absolute inset-0 bg-gradient-to-br from-transparent to-white opacity-20"
                  animate={{ 
                    rotate: [0, 360],
                  }}
                  transition={{ 
                    duration: 10,
                    repeat: Infinity,
                    ease: "linear"
                  }}
                />
                <div dangerouslySetInnerHTML={{ __html: selectedAvatar?.svg || '' }} />
              </motion.div>
              <div>
                <motion.h1 
                  className="text-2xl font-bold"
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  {persona.name}
                </motion.h1>
                <motion.p 
                  className={`text-sm ${styleClasses.accent}`}
                  initial={{ opacity: 0, y: 10 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.4 }}
                >
                  {persona.role}
                </motion.p>
              </div>
            </motion.div>
            <motion.button
              onClick={() => setIsEditing(!isEditing)}
              className={`px-4 py-2 rounded-lg ${styleClasses.button} transition-colors duration-200 relative overflow-hidden`}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <motion.span
                className="absolute inset-0 bg-white opacity-0"
                whileHover={{ opacity: 0.1 }}
              />
              {isEditing ? 'Сохранить' : 'Редактировать'}
            </motion.button>
          </div>
        </div>

        <div className="p-6">
          <AnimatePresence mode="wait">
            {isEditing ? (
              <motion.div
                key="edit"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-4"
              >
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                    Имя
                  </label>
                  <motion.input
                    type="text"
                    value={editedPersona?.name || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, name: e.target.value })}
                    className={`w-full p-2 rounded-lg border ${styleClasses.card} ${styleClasses.content} focus:outline-none focus:ring-2 focus:ring-opacity-50 focus:ring-${persona.style}-300`}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                    Роль
                  </label>
                  <motion.input
                    type="text"
                    value={editedPersona?.role || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, role: e.target.value })}
                    className={`w-full p-2 rounded-lg border ${styleClasses.card} ${styleClasses.content} focus:outline-none focus:ring-2 focus:ring-opacity-50 focus:ring-${persona.style}-300`}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: 0.3 }}
                >
                  <label className={`block text-sm font-medium ${styleClasses.accent} mb-1`}>
                    Описание
                  </label>
                  <motion.textarea
                    value={editedPersona?.description || ''}
                    onChange={(e) => setEditedPersona({ ...editedPersona!, description: e.target.value })}
                    className={`w-full p-2 rounded-lg border ${styleClasses.card} ${styleClasses.content} focus:outline-none focus:ring-2 focus:ring-opacity-50 focus:ring-${persona.style}-300`}
                    rows={4}
                    whileFocus={{ scale: 1.01 }}
                  />
                </motion.div>
                <motion.button
                  onClick={handleSave}
                  className={`w-full px-4 py-2 rounded-lg ${styleClasses.button} transition-colors duration-200 relative overflow-hidden`}
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <motion.span
                    className="absolute inset-0 bg-white opacity-0"
                    whileHover={{ opacity: 0.1 }}
                  />
                  Сохранить изменения
                </motion.button>
              </motion.div>
            ) : (
              <motion.div
                key="view"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className={`space-y-4 ${styleClasses.content}`}
              >
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 }}
                >
                  <h2 className={`text-lg font-semibold ${styleClasses.accent} mb-2`}>
                    О персонаже
                  </h2>
                  <motion.p 
                    className="text-justify"
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    transition={{ delay: 0.2 }}
                  >
                    {persona.description}
                  </motion.p>
                </motion.div>
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.2 }}
                >
                  <h2 className={`text-lg font-semibold ${styleClasses.accent} mb-2`}>
                    Характеристики
                  </h2>
                  <div className="grid grid-cols-2 gap-4">
                    {persona.characteristics.map((char: Characteristic, index: number) => (
                      <motion.div
                        key={index}
                        className="flex items-center space-x-2 p-2 rounded-lg hover:bg-opacity-10 hover:bg-current relative overflow-hidden"
                        initial={{ opacity: 0, x: -20 }}
                        animate={{ opacity: 1, x: 0 }}
                        transition={{ delay: 0.1 * index }}
                        whileHover={{ scale: 1.02 }}
                      >
                        <motion.span
                          className="absolute inset-0 bg-white opacity-0"
                          whileHover={{ opacity: 0.1 }}
                        />
                        <span className="font-medium">{char.name}:</span>
                        <span>{char.value}</span>
                      </motion.div>
                    ))}
                  </div>
                </motion.div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.div>
    </div>
  );
};

export default Card;
