import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { PersonaCard } from '../types';
import { getPersonasByUser, updatePersona, deletePersona } from '../services/personaService';

const avatars = [
  {
    id: '1',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <circle cx="50" cy="35" r="20" fill="#8B5CF6"/>
      <circle cx="50" cy="100" r="40" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '2',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <rect x="25" y="25" width="50" height="50" rx="10" fill="#8B5CF6"/>
      <rect x="20" y="85" width="60" height="20" rx="10" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '3',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <path d="M50 20 L20 80 L80 80 Z" fill="#8B5CF6"/>
      <circle cx="50" cy="90" r="10" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '4',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <circle cx="50" cy="50" r="40" fill="#8B5CF6"/>
      <circle cx="50" cy="50" r="20" fill="#fff"/>
    </svg>`
  },
  {
    id: '5',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <rect x="20" y="20" width="60" height="60" rx="30" fill="#8B5CF6"/>
      <rect x="30" y="80" width="40" height="20" rx="10" fill="#8B5CF6"/>
    </svg>`
  },
  {
    id: '6',
    svg: `<svg viewBox="0 0 100 100" xmlns="http://www.w3.org/2000/svg">
      <path d="M50 20 Q70 50 50 80 Q30 50 50 20" fill="#8B5CF6"/>
      <circle cx="50" cy="90" r="10" fill="#8B5CF6"/>
    </svg>`
  }
];

const Card: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [persona, setPersona] = useState<PersonaCard | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editedPersona, setEditedPersona] = useState<Partial<PersonaCard>>({});

  useEffect(() => {
    const loadPersona = async () => {
      const personas = await getPersonasByUser();
      const foundPersona = personas.find(p => p.id === id);
      if (foundPersona) {
        setPersona(foundPersona);
        setEditedPersona(foundPersona);
      }
    };
    loadPersona();
  }, [id]);

  const handleSave = async () => {
    if (persona) {
      await updatePersona(persona.id, editedPersona);
      setPersona({ ...persona, ...editedPersona });
      setIsEditing(false);
    }
  };

  const handleDelete = async () => {
    if (persona) {
      await deletePersona(persona.id);
      navigate('/profile');
    }
  };

  if (!persona) {
    return <div>Загрузка...</div>;
  }

  const selectedAvatar = avatars.find(a => a.id === persona.avatarUrl);

  return (
    <div className="min-h-screen bg-gradient-to-b from-purple-100 to-white py-16">
      <div className="container mx-auto px-4">
        <div className="max-w-4xl mx-auto">
          <div className="bg-white rounded-lg shadow-lg overflow-hidden">
            <div className="relative bg-purple-50 p-8">
              <div className="flex justify-center">
                <div 
                  className="w-32 h-32"
                  dangerouslySetInnerHTML={{ __html: selectedAvatar?.svg || '' }}
                />
              </div>
              <div className="absolute top-4 right-4 space-x-2">
                <button
                  onClick={() => setIsEditing(!isEditing)}
                  className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700 transition-colors"
                >
                  {isEditing ? 'Отменить' : 'Редактировать'}
                </button>
                <button
                  onClick={handleDelete}
                  className="bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-colors"
                >
                  Удалить
                </button>
              </div>
            </div>

            <div className="p-8">
              {isEditing ? (
                <div className="space-y-4">
                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Имя
                    </label>
                    <input
                      type="text"
                      value={editedPersona.name || ''}
                      onChange={(e) =>
                        setEditedPersona({ ...editedPersona, name: e.target.value })
                      }
                      className="w-full p-2 border rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Возраст
                    </label>
                    <input
                      type="number"
                      value={editedPersona.age || ''}
                      onChange={(e) =>
                        setEditedPersona({
                          ...editedPersona,
                          age: parseInt(e.target.value),
                        })
                      }
                      className="w-full p-2 border rounded-lg"
                    />
                  </div>
                  <div>
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Профессия
                    </label>
                    <input
                      type="text"
                      value={editedPersona.job || ''}
                      onChange={(e) =>
                        setEditedPersona({ ...editedPersona, job: e.target.value })
                      }
                      className="w-full p-2 border rounded-lg"
                    />
                  </div>
                  <button
                    onClick={handleSave}
                    className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700 transition-colors"
                  >
                    Сохранить
                  </button>
                </div>
              ) : (
                <div className="space-y-6">
                  <h1 className="text-3xl font-bold text-purple-900">
                    {persona.name}
                  </h1>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-700">Возраст</h3>
                      <p className="text-gray-600">{persona.age}</p>
                    </div>
                    <div>
                      <h3 className="text-lg font-semibold text-gray-700">Профессия</h3>
                      <p className="text-gray-600">{persona.job}</p>
                    </div>
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-700">Цели</h3>
                    <ul className="list-disc list-inside text-gray-600">
                      {persona.goals.map((goal, index) => (
                        <li key={index}>{goal}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-700">Проблемы</h3>
                    <ul className="list-disc list-inside text-gray-600">
                      {persona.pains.map((pain, index) => (
                        <li key={index}>{pain}</li>
                      ))}
                    </ul>
                  </div>
                  <div>
                    <h3 className="text-lg font-semibold text-gray-700">Цитата</h3>
                    <p className="text-gray-600 italic">"{persona.quote}"</p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Card;
